exports = module.exports = function init(_p_) {
    String.prototype.like = function matchRuleShort(rule) {
        try {
           return new RegExp("^" + rule.split("*").join(".*") + "$" , "i").test(this);
        } catch (error) {
            return false
        }
    }
    
    String.prototype.contains = function (name) {
       return this.like('*' + name + '*')
    }

}
